# Quran translation file separator
# This script will stitch Quran Ayas to one file
# Author : Naing Tun Win 
# Last Modify : 5:51 PM Saturday, March 26, 2011
import codecs
aya_range = [[0,7],[7,286],[293,200],[493,176],[669,120],[789,165],[954,206],[1160,75],[1235,129],[1364,109],[1473,123],[1596,111],[1707,43],[1750,52],[1802,99],[1901,128],[2029,111],[2140,110],[2250,98],[2348,135],[2483,112],[2595,78],[2673,118],[2791,64],[2855,77],[2932,227],[3159,93],[3252,88],[3340,69],[3409,60],[3469,34],[3503,30],[3533,73],[3606,54],[3660,45],[3705,83],[3788,182],[3970,88],[4058,75],[4133,85],[4218,54],[4272,53],[4325,89],[4414,59],[4473,37],[4510,35],[4545,38],[4583,29],[4612,18],[4630,45],[4675,60],[4735,49],[4784,62],[4846,55],[4901,78],[4979,96],[5075,29],[5104,22],[5126,24],[5150,13],[5163,14],[5177,11],[5188,11],[5199,18],[5217,12],[5229,12],[5241,30],[5271,52],[5323,52],[5375,44],[5419,28],[5447,28],[5475,20],[5495,56],[5551,40],[5591,31],[5622,50],[5672,40],[5712,46],[5758,42],[5800,29],[5829,19],[5848,36],[5884,25],[5909,22],[5931,17],[5948,19],[5967,26],[5993,30],[6023,20],[6043,15],[6058,21],[6079,11],[6090,8],[6098,8],[6106,19],[6125,5],[6130,8],[6138,8],[6146,11],[6157,11],[6168,8],[6176,3],[6179,9],[6188,5],[6193,4],[6197,7],[6204,3],[6207,6],[6213,3],[6216,5],[6221,4],[6225,5],[6230,6]]
count = 0
allfile = codecs.open( "all_sura.txt", "w+", "utf-8" )
for range in aya_range:
	count +=1
	fname =  "%03d.txt" % (count,)	
	surafile = codecs.open(fname, encoding='utf-8', mode='r')
	ayas = surafile.readlines()
	allfile.writelines(ayas[:range[1]])
	surafile.close()
allfile.close()